r.table('marvel').map {|hero|
  hero[:monsters_killed] + hero[:super_villains_vanquished]
}.reduce(0) {|acc, val|
  acc + val
}.run(c)

r.table('marvel').groupedMapReduce(function(hero) {
 return hero('weightClass')
}, function(hero) {
 return hero.pluck('name', 'strength')
}, function(acc, hero) {
 return r.branch(acc('strength').lt(hero('strength')), hero, acc)
}, {name:'none', strength:0}).run(c)

r.table('marvel').grouped_map_reduce(lambda hero: hero['weightClass'],
  lambda hero: hero.pluck('name', 'strength'),
  lambda acc, hero: r.branch(acc['strength'] < hero['strength'], hero, acc),
  {'name':'none', 'strength':0}
).run(c)

r.table('marvel').grouped_map_reduce(lambda {|hero|
 hero[:weight_class]
}, lambda {|hero|
 hero.pluck(:name, :strength)
}, {:name => 'none', :strength => 0}, lambda {|acc, hero|
 r.branch(acc[:strength] < hero[:strength], hero, acc)
}).run(c)

r.do(r.table('marvel').get('IronMan'),
 r.table('marvel').get('Thor'),
 function(ironman, thor) {
  return r.branch(ironman('manliness').gt(thor('manliness')), ironman, thor)
})

r.do(r.table('marvel').get('IronMan'), r.table('marvel').get('Thor')) {
  r.branch(ironman[:manliness] > thor[:manliness], ironman thor)
}.run(c)

r.table('marvel').get('IronMan').do {
  r.branch(ironman[:victories] < ironman[:battles],
    r.error('impossible code path'),
    ironman)
}.run(c)
